/**
* Creates a benchmark function for measuring WASM interface and using heap `setValue` method.
*
* @private
* @param {PositiveInteger} len - array length
* @returns {Function} benchmark function
*/
function createBenchmark3( len ) {
	var idx;
	var x;
	var i;

	x = new Float64Array( len );
	for ( i = 0; i < x.length; i++ ) {
		x[ i ] = ( randu()*20000.0 ) - 10000.0;
	}
	idx = floor( len/10 * x.BYTES_PER_ELEMENT );

	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var nbytes;
		var bytes;
		var y;
		var v;
		var i;

		nbytes = x.length * x.BYTES_PER_ELEMENT;
		bytes = wasm.malloc( nbytes );
		bytes.set( new Uint8Array( x.buffer ) );

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			v = ( randu()*20000.0 ) - 10000.0;
			bytes.setValue( idx, v );
			y = wasm( x.length, bytes, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		b.pass( 'benchmark finished' );

		wasm.free( bytes );
		b.end();
	} // end FUNCTION benchmark()
} // end FUNCTION createBenchmark3()

/**
* Creates a benchmark function for measuring WASM interface and using heap view.
*
* @private
* @param {PositiveInteger} len - array length
* @returns {Function} benchmark function
*/
function createBenchmark4( len ) {
	var idx;
	var x;
	var i;

	x = new Float64Array( len );
	for ( i = 0; i < x.length; i++ ) {
		x[ i ] = ( randu()*20000.0 ) - 10000.0;
	}
	idx = floor( len / 10 );

	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var nbytes;
		var bytes;
		var view;
		var y;
		var v;
		var i;

		nbytes = x.length * x.BYTES_PER_ELEMENT;
		bytes = wasm.malloc( nbytes );
		bytes.set( new Uint8Array( x.buffer ) );

		view = new Float64Array( bytes.buffer, bytes.byteOffset, x.length );

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			v = ( randu()*20000.0 ) - 10000.0;
			view[ idx ] = v;
			y = wasm( x.length, bytes, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		b.pass( 'benchmark finished' );

		wasm.free( bytes );
		b.end();
	} // end FUNCTION benchmark()
} // end FUNCTION createBenchmark4()


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var len;
	var min;
	var max;
	var f;
	var i;

	min = 1; // 10^min
	max = 6; // 10^max

	for ( i = min; i <= max; i++ ) {
		len = pow( 10, i );

		f = createBenchmark1( len );
		bench( pkg+'::memory-reuse,main:len='+len, f );

		f = createBenchmark2( len );
		bench( pkg+'::memory-reuse,native:len='+len, opts, f );

		f = createBenchmark3( len );
		bench( pkg+'::memory-reuse,wasm,set-value:len='+len, f );

		f = createBenchmark4( len );
		bench( pkg+'::memory-reuse,wasm,view:len='+len, f );
	}
} // end FUNCTION main()

main();
