tape( 'the returned function supports view offsets', function test( t ) {
	var expected;
	var xbytes;
	var ybytes;
	var daxpy;
	var view;
	var x0;
	var y0;
	var x1;
	var y1;
	var N;
	var i;

	daxpy = factory();

	// Initial arrays...
	x0 = new Float64Array([
		1.0,
		2.0, // 2
		3.0,
		4.0, // 1
		5.0,
		6.0  // 0
	]);
	y0 = new Float64Array([
		7.0,
		8.0,
		9.0,
		10.0, // 0
		11.0, // 1
		12.0  // 2
	]);

	// Create offset views...
	x1 = new Float64Array( x0.buffer, x0.BYTES_PER_ELEMENT*1 ); // begin at 2nd element
	y1 = new Float64Array( y0.buffer, y0.BYTES_PER_ELEMENT*3 ); // begin at the 4th element

	N = floor( x0.length / 2 );

	xbytes = daxpy.malloc( x1.length * x1.BYTES_PER_ELEMENT );
	xbytes.set( new Uint8Array( x1.buffer, x1.byteOffset ) );

	ybytes = daxpy.malloc( y1.length * y1.BYTES_PER_ELEMENT );
	ybytes.set( new Uint8Array( y1.buffer, y1.byteOffset ) );

	daxpy( N, 3.0, xbytes, -2, ybytes, 1 );

	expected = new Float64Array([
		7.0,
		8.0,
		9.0,
		28.0,
		23.0,
		18.0
	]);

	view = new Float64Array( ybytes.buffer, ybytes.byteOffset, y1.length );
	for ( i = 0; i < y1.length; i++ ) {
		y1[ i ] = view[ i ];
	}
	t.deepEqual( y0, expected, 'deep equal' );

	daxpy.free( xbytes );
	daxpy.free( ybytes );

	t.end();
});

tape( 'if both strides are equal to `1`, the returned function efficiently multiplies `x` by a constant and adds to `y`', function test( t ) {
	var expected;
	var xbytes;
	var ybytes;
	var daxpy;
	var alpha;
	var view;
	var x;
	var y;
	var i;

	daxpy = factory();

	alpha = 3.0;

	x = new Float64Array( 100 );
	y = new Float64Array( x.length );
	expected = new Float64Array( x.length );
	for ( i = 0; i < x.length; i++ ) {
		x[ i ] = i;
		y[ i ] = x.length - i;
		expected[ i ] = ( x[i]*alpha ) + y[i];
	}

	xbytes = daxpy.malloc( x.length * x.BYTES_PER_ELEMENT );
	xbytes.set( new Uint8Array( x.buffer ) );

	ybytes = daxpy.malloc( y.length * y.BYTES_PER_ELEMENT );
	ybytes.set( new Uint8Array( y.buffer ) );

	daxpy( x.length, alpha, xbytes, 1, ybytes, 1 );

	view = new Float64Array( ybytes.buffer, ybytes.byteOffset, y.length );
	for ( i = 0; i < y.length; i++ ) {
		y[ i ] = view[ i ];
	}
	t.deepEqual( y, expected, 'deep equal' );

	daxpy.free( xbytes );
	daxpy.free( ybytes );

	x = new Float64Array( 123 );
	y = new Float64Array( x.length );
	expected = new Float64Array( x.length );
	for ( i = 0; i < x.length; i++ ) {
		x[ i ] = i*2;
		y[ i ] = x.length - i;
		expected[ i ] = ( x[i]*alpha ) + y[i];
	}

	xbytes = daxpy.malloc( x.length * x.BYTES_PER_ELEMENT );
	xbytes.set( new Uint8Array( x.buffer ) );

	ybytes = daxpy.malloc( y.length * y.BYTES_PER_ELEMENT );
	ybytes.set( new Uint8Array( y.buffer ) );

	daxpy( x.length, alpha, xbytes, 1, ybytes, 1 );

	view = new Float64Array( ybytes.buffer, ybytes.byteOffset, y.length );
	for ( i = 0; i < y.length; i++ ) {
		y[ i ] = view[ i ];
	}
	t.deepEqual( y, expected, 'deep equal' );

	daxpy.free( xbytes );
	daxpy.free( ybytes );

	t.end();
});

