'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isBrowser = require( '@stdlib/assert/is-browser' );
var daxpy = require( './../lib' );


// VARIABLES //

var opts = {
	'skip': isBrowser
};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof daxpy, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to copy `ndarray` elements', function test( t ) {
	t.strictEqual( typeof daxpy.ndarray, 'function', 'method is a function' );
	t.end();
});

tape( 'attached to the main export is a method providing a WebAssembly interface', function test( t ) {
	t.strictEqual( typeof daxpy.wasm, 'function', 'method is a function' );
	t.end();
});

tape( 'if a native implementation is available, the main export is the native implementation', opts, function test( t ) {
	var daxpy = proxyquire( './../lib', {
		'@stdlib/utils/try-require': tryRequire
	});

	t.strictEqual( daxpy, mock, 'returns native implementation' );
	t.end();

	function tryRequire() {
		return mock;
	}

	function mock() {
		// Mock...
	}
});

tape( 'if a native implementation is not available, the main export is a JavaScript implementation', opts, function test( t ) {
	var daxpy;
	var main;

	main = require( './../lib/daxpy.js' );

	daxpy = proxyquire( './../lib', {
		'@stdlib/utils/try-require': tryRequire
	});

	t.strictEqual( daxpy, main, 'returns JavaScript implementation' );
	t.end();

	function tryRequire() {
		return new Error( 'Cannot find module' );
	}
});

tape( 'if in a browser environment, the main export is a JavaScript implementation', opts, function test( t ) {
	var daxpy;
	var main;

	main = require( './../lib/daxpy.js' );

	daxpy = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': true
	});

	t.strictEqual( daxpy, main, 'returns JavaScript implementation' );
	t.end();
});

