'use strict';

/**
* Round a complex number toward positive infinity.
*
* @module @stdlib/math/base/complex/ceil
*
* @example
* var cceil = require( '@stdlib/math/base/complex/ceil' );
*
* var v = cceil( -4.2, 5.5 );
* // returns [ -4.0, 6.0 ]
*
* v = cceil( 9.99999, 0.1 );
* // returns [ 10.0, 1.0 ]
*
* v = cceil( 0.0, 0.0 );
* // returns [ 0.0, 0.0 ]
*
* v = cceil( NaN, NaN );
* // returns [ NaN, NaN ]
*/

// MODULES //

var cceil = require( './cceil.js' );


// EXPORTS //

module.exports = cceil;
