'use strict';

// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );


// MAIN //

/**
* Divides two complex numbers using the method of Smith.
*
* ## References
*
* * Baudin, Michael, and Robert L. Smith. 2012. "A Robust Complex Division in Scilab" abs/1210.4539 (October). <https://arxiv.org/abs/1210.4539>.
*
* @param {number} re1 - real component
* @param {number} im1 - imaginary component
* @param {number} re2 - real component
* @param {number} im2 - imaginary component
* @returns {Array<number>} real and imaginary components
*
* @example
* var v = cdiv( -13.0, -1.0, -2.0, 1.0 );
* // returns [ 5.0, 3.0 ]
*/
function cdiv( re1, im1, re2, im2 ) {
	var a;
	var b;

	if ( abs(re2) >= abs(im2) ) {
		a = im2 / re2;
		b = re2 + ( im2 * a );
		return [
			(re1 + ( im1 * a ) ) / b,
			(im1 - ( re1 * a ) ) / b
		];
	}

	a = re2 / im2;
	b = ( re2 * a ) + im2;
	return [
		( ( re1 * a ) + im1) / b,
		( ( im1 * a ) - re1) / b
	];
} // end FUNCTION cdiv()


// EXPORTS //

module.exports = cdiv;
