'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var isNegativeZero = require( '@stdlib/math/base/assert/is-negative-zero' );
var abs = require( '@stdlib/math/base/special/abs' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var PI = require( '@stdlib/math/constants/float64-pi' );
var cphase = require( './../lib' );


// FIXTURES //

var positivePositive = require( './fixtures/julia/positive_positive.json' );
var negativePositive = require( './fixtures/julia/negative_positive.json' );
var negativeNegative = require( './fixtures/julia/negative_negative.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof cphase, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `NaN` if provided `NaN` as either of the arguments', function test( t ) {
	t.strictEqual( isnan( cphase( 2.0, NaN ) ), true, 'returns NaN' );
	t.strictEqual( isnan( cphase( NaN, 3.0 ) ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `+0` if provided `im = +0.0` and `re >= 0`', function test( t ) {
	t.strictEqual( cphase( 0.0, +0.0 ), +0.0, 'returns +0' );
	t.strictEqual( cphase( 2.0, +0.0 ), +0.0, 'returns +0' );
	t.strictEqual( cphase( 4.0, +0.0 ), +0.0, 'returns +0' );
	t.strictEqual( cphase( 5.0, +0.0 ), +0.0, 'returns +0' );
	t.strictEqual( cphase( 10.0, +0.0 ), +0.0, 'returns +0' );
	t.end();
});

tape( 'the function returns `-0` if provided `im = -0.0` and `re >= 0`', function test( t ) {
	t.strictEqual( isNegativeZero( cphase( 0.0, -0.0 ) ), true, 'returns -0' );
	t.strictEqual( isNegativeZero( cphase( 2.0, -0.0 ) ), true, 'returns -0' );
	t.strictEqual( isNegativeZero( cphase( 4.0, -0.0 ) ), true, 'returns -0' );
	t.strictEqual( isNegativeZero( cphase( 5.0, -0.0 ) ), true, 'returns -0' );
	t.strictEqual( isNegativeZero( cphase( 10.0, -0.0 ) ), true, 'returns -0' );
	t.end();
});

tape( 'the function returns `PI` if provided `im = +0.0` and `re <= -0.0`', function test( t ) {
	t.strictEqual( cphase( -0.0, +0.0 ), +PI, 'returns +PI' );
	t.strictEqual( cphase( -2.0, +0.0 ), +PI, 'returns +PI' );
	t.strictEqual( cphase( -4.0, +0.0 ), +PI, 'returns +PI' );
	t.strictEqual( cphase( -5.0, +0.0 ), +PI, 'returns +PI' );
	t.strictEqual( cphase( -10.0, +0.0 ), +PI, 'returns +PI' );
	t.end();
});

tape( 'the function returns `-PI` if provided `im = -0.0` and `re <= -0.0`', function test( t ) {
	t.strictEqual( cphase( -0.0, -0.0 ), -PI, 'returns -PI' );
	t.strictEqual( cphase( -2.0, -0.0 ), -PI, 'returns -PI' );
	t.strictEqual( cphase( -4.0, -0.0 ), -PI, 'returns -PI' );
	t.strictEqual( cphase( -5.0, -0.0 ), -PI, 'returns -PI' );
	t.strictEqual( cphase( -10.0, -0.0 ), -PI, 'returns -PI' );
	t.end();
});

tape( 'the function returns `+PI/4` if provided `re = im = +infinity`', function test( t ) {
	t.strictEqual( cphase( PINF, PINF ), +PI/4, 'returns +PI/4' );
	t.end();
});

tape( 'the function returns `-PI/4` if provided `re = -im = +infinity`', function test( t ) {
	t.strictEqual( cphase( PINF, NINF ), -PI/4, 'returns -PI/4' );
	t.end();
});

tape( 'the function returns `*3*PI/4` if provided `-re = im = +infinity`', function test( t ) {
	t.strictEqual( cphase( NINF, PINF ), +3*PI/4, 'returns +3*PI/4' );
	t.end();
});

tape( 'the function returns `-3*PI/4` if provided `re = im = -infinity`', function test( t ) {
	t.strictEqual( cphase( NINF, NINF ), -3*PI/4, 'returns -3*PI/4' );
	t.end();
});

tape( 'the function returns `0.0` when `re = +infinity`', function test( t ) {
	t.strictEqual( cphase( PINF, -2.0 ), 0.0, 'returns 0.0' );
	t.strictEqual( cphase( PINF, 0.0 ), 0.0, 'returns 0.0' );
	t.strictEqual( cphase( PINF, 2.0 ), 0.0, 'returns 0.0' );
	t.end();
});

tape( 'the function returns `+PI` when `im > 0` and `re = -infinity`', function test( t ) {
	t.strictEqual( cphase( NINF, 1.0 ), PI, 'returns PI' );
	t.strictEqual( cphase( NINF, 2.0 ), PI, 'returns PI' );
	t.strictEqual( cphase( NINF, 3.0 ), PI, 'returns PI' );
	t.end();
});

tape( 'the function returns `-PI` when `im < 0` and `re = -infinity`', function test( t ) {
	t.strictEqual( cphase( NINF, -1.0 ), -PI, 'returns -PI' );
	t.strictEqual( cphase( NINF, -2.0 ), -PI, 'returns -PI' );
	t.strictEqual( cphase( NINF, -3.0 ), -PI, 'returns -PI' );
	t.end();
});

tape( 'the function returns `+PI/2` when `im = +infinity`', function test( t ) {
	t.strictEqual( cphase( -1.0, PINF ), PI/2, 'returns PI/2' );
	t.strictEqual( cphase( 0.0, PINF ), PI/2, 'returns PI/2' );
	t.strictEqual( cphase( 2.0, PINF ), PI/2, 'returns PI/2' );
	t.end();
});

tape( 'the function returns `-PI/2` when `im = -infinity`', function test( t ) {
	t.strictEqual( cphase( -1.0, NINF ), -PI/2, 'returns -PI/2' );
	t.strictEqual( cphase( 0.0, NINF ), -PI/2, 'returns -PI/2' );
	t.strictEqual( cphase( 2.0, NINF ), -PI/2, 'returns -PI/2' );
	t.end();
});

tape( 'the function returns `PI/2` if provided a positive `im` and `re=0`', function test( t ) {
	t.strictEqual( cphase( 0.0, 2.0 ), PI/2, 'returns PI/2' );
	t.strictEqual( cphase( 0.0, 1.0 ), PI/2, 'returns PI/2' );
	t.strictEqual( cphase( 0.0, 0.5 ), PI/2, 'returns PI/2' );
	t.end();
});

tape( 'the function returns `-PI/2` if provided a negative `im` and `re=0`', function test( t ) {
	t.strictEqual( cphase( 0.0, -2.0 ), -PI/2, 'returns PI/2' );
	t.strictEqual( cphase( 0.0, -1.0 ), -PI/2, 'returns PI/2' );
	t.strictEqual( cphase( 0.0, -0.5 ), -PI/2, 'returns PI/2' );
	t.end();
});

tape( 'the function computes the argument of a complex number (when `re` and `im` are positive)', function test( t ) {
	var expected;
	var actual;
	var delta;
	var tol;
	var re;
	var im;
	var i;

	re = positivePositive.re;
	im = positivePositive.im;
	expected = positivePositive.expected;
	for ( i = 0; i < re.length; i++ ) {
		actual = cphase( re[i], im[i] );
		delta = abs( actual - expected[i] );
		tol = EPS * abs( expected[i] );
		t.strictEqual( delta <= tol, true, 'within tolerance. re: '+re[i]+'. im: '+im[i]+'. Actual: '+actual+'. Expected: '+expected[i]+'. tol: '+tol+'. delta: '+delta+'.' );
	}
	t.end();
});

tape( 'the function computes the argument of a complex number (when `re` is negative and `im` is positive)', function test( t ) {
	var expected;
	var actual;
	var delta;
	var tol;
	var re;
	var im;
	var i;

	re = negativePositive.re;
	im = negativePositive.im;
	expected = negativePositive.expected;
	for ( i = 0; i < re.length; i++ ) {
		actual = cphase( re[i], im[i] );
		delta = abs( actual - expected[i] );
		tol = 2.0 * EPS * abs( expected[i] );
		t.strictEqual( delta <= tol, true, 'within tolerance. re: '+re[i]+'. im: '+im[i]+'. Actual: '+actual+'. Expected: '+expected[i]+'. tol: '+tol+'. delta: '+delta+'.' );
	}
	t.end();
});

tape( 'the function computes the argument of a complex number (when `re` and `im` are negative)', function test( t ) {
	var expected;
	var actual;
	var delta;
	var tol;
	var re;
	var im;
	var i;

	re = negativeNegative.re;
	im = negativeNegative.im;
	expected = negativeNegative.expected;
	for ( i = 0; i < re.length; i++ ) {
		actual = cphase( re[i], im[i] );
		delta = abs( actual - expected[i] );
		tol = 2.0 * EPS * abs( expected[i] );
		t.strictEqual( delta <= tol, true, 'within tolerance. re: '+re[i]+'. im: '+im[i]+'. Actual: '+actual+'. Expected: '+expected[i]+'. tol: '+tol+'. delta: '+delta+'.' );
	}
	t.end();
});
