'use strict';

// MODULES //

var tape = require( 'tape' );
var PINF = require( '@stdlib/constants/math/float64-pinf' );
var NINF = require( '@stdlib/constants/math/float64-ninf' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var isNegativeZero = require( '@stdlib/math/base/assert/is-negative-zero' );
var isPositiveZero = require( '@stdlib/math/base/assert/is-positive-zero' );
var Float64Array = require( '@stdlib/array/float64' );
var csig = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof csig, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function calculates the signum function of argument', function test( t ) {
	var expected;
	var actual;

	actual = csig( -4.2, 5.5 );
	expected = [ -0.6069136033622302, 0.79476781392673 ];
	t.deepEqual( actual, expected, 'returns expected value' );

	actual = csig( 9.99999, 0.1 );
	expected = [ 0.9999500036497024, 0.00999951003600706 ];
	t.deepEqual( actual, expected, 'returns expected value' );

	actual = csig( 4.0, 7.0 );
	expected = [ 0.49613893835683387, 0.8682431421244593 ];
	t.deepEqual( actual, expected, 'returns expected value' );

	actual = csig( -4.0, -7.0 );
	expected = [ -0.49613893835683387, -0.8682431421244593 ];
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function calculates the signum function of argument (output array)', function test( t ) {
	var expected;
	var actual;
	var out;

	out = new Array( 2 );
	actual = csig( out, -4.2, 5.5 );

	expected = [ -0.6069136033622302, 0.79476781392673 ];

	t.deepEqual( actual, expected, 'returns expected value' );
	t.strictEqual( actual, out, 'returns output value' );

	t.end();
});

tape( 'the function calculates the signum function of argument (output typed array)', function test( t ) {
	var expected;
	var actual;
	var out;

	out = new Float64Array( 2 );
	actual = csig( out, 9.99999, 0.1 );

	expected = new Float64Array( [ 0.9999500036497024, 0.00999951003600706 ] );

	t.deepEqual( actual, expected, 'returns expected value' );
	t.strictEqual( actual, out, 'returns output value' );

	t.end();
});

tape( 'the function calculates the signum function of argument (output object)', function test( t ) {
	var expected;
	var actual;
	var out;

	out = {};
	actual = csig( out, 4.2, -5.5 );

	expected = {
		'0': 0.6069136033622302,
		'1': -0.79476781392673
	};

	t.deepEqual( actual, expected, 'returns expected value' );
	t.strictEqual( actual, out, 'returns output value' );

	t.end();
});

tape( 'the function returns a `NaN` if provided a `NaN`', function test( t ) {
	var val = csig( NaN, NaN );
	t.strictEqual( isnan( val[ 0 ] ), true, 'returns expected value' );
	t.strictEqual( isnan( val[ 1 ] ), true, 'returns expected value' );
	t.end();
});

tape( 'the function returns `+0` if provided `+0`', function test( t ) {
	var val = csig( +0.0, +0.0 );
	t.strictEqual( isPositiveZero( val[ 0 ] ), true, 'returns expected value' );
	t.strictEqual( isPositiveZero( val[ 1 ] ), true, 'returns expected value' );
	t.end();
});

tape( 'the function returns `-0` if provided `-0`', function test( t ) {
	var val = csig( -0.0, -0.0 );
	t.strictEqual( isNegativeZero( val[ 0 ] ), true, 'returns expected value' );
	t.strictEqual( isNegativeZero( val[ 1 ] ), true, 'returns expected value' );
	t.end();
});

tape( 'the function returns a `NaN` if provided `infinity`', function test( t ) {
	var val = csig( PINF, PINF );
	t.strictEqual( isnan( val[ 0 ] ), true, 'returns expected value' );
	t.strictEqual( isnan( val[ 1 ] ), true, 'returns expected value' );
	t.end();
});

tape( 'the function returns a `NaN` if provided `-infinity`', function test( t ) {
	var val = csig( NINF, NINF );
	t.strictEqual( isnan( val[ 0 ] ), true, 'returns expected value' );
	t.strictEqual( isnan( val[ 1 ] ), true, 'returns expected value' );
	t.end();
});
