'use strict';

// MODULES //

var betainc = require( '@stdlib/math/base/special/betainc' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for a beta distribution with first shape parameter `alpha` and second shape parameter `beta` at a value `x`.
*
* @param {number} x - input value
* @param {PositiveNumber} alpha - first shape parameter
* @param {PositiveNumber} beta - second shape parameter
* @returns {number} evaluated CDF
*
* @example
* var y = cdf( 0.5, 1.0, 1.0 );
* // returns 0.5
* @example
* var y = cdf( 0.5, 2.0, 4.0 );
* // returns ~0.813
* @example
* var y = cdf( 0.2, 2.0, 2.0 );
* // returns ~0.104
* @example
* var y = cdf( 0.8, 4.0, 4.0 );
* // returns ~0.967
* @example
* var y = cdf( -0.5, 4.0, 2.0 );
* // returns 0.0
* @example
* var y = cdf( 1.5, 4.0, 2.0 );
* // returns 1.0
* @example
* var y = cdf( 2.0, -1.0, 0.5 );
* // returns NaN
* @example
* var y = cdf( 2.0, 0.5, -1.0 );
* // returns NaN
* @example
* var y = cdf( NaN, 1.0, 1.0 );
* // returns NaN
* @example
* var y = cdf( 0.0, NaN, 1.0 );
* // returns NaN
* @example
* var y = cdf( 0.0, 1.0, NaN );
* // returns NaN
*/
function cdf( x, alpha, beta ) {
	if (
		isnan( x ) ||
		isnan( alpha ) ||
		isnan( beta ) ||
		alpha <= 0.0 ||
		beta <= 0.0
	) {
		return NaN;
	}
	if ( x <= 0.0 ) {
		return 0.0;
	}
	if ( x >= 1.0 ) {
		return 1.0;
	}
	return betainc( x, alpha, beta );
} // end FUNCTION cdf()


// EXPORTS //

module.exports = cdf;
