'use strict';

// MODULES //

var betaln = require( '@stdlib/math/base/special/betaln');
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var log1p = require( '@stdlib/math/base/special/log1p' );
var ln = require( '@stdlib/math/base/special/ln' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );


// PDF //

/**
* Evaluates the logarithm of the probability density function (PDF) for a beta distribution with first shape parameter `alpha` and second shape parameter `beta` at a value `x`.
*
* @param {number} x - input value
* @param {PositiveNumber} alpha - first shape parameter
* @param {PositiveNumber} beta - second shape parameter
* @returns {number} evaluated logPDF
*
* @example
* var y = logpdf( 0.5, 1.0, 1.0 );
* // returns 0.0
* @example
* var y = logpdf( 0.5, 2.0, 4.0 );
* // returns ~0.223
* @example
* var y = logpdf( 0.2, 2.0, 2.0 );
* // returns ~-0.041
* @example
* var y = logpdf( 0.8, 4.0, 4.0 );
* // returns ~-0.557
* @example
* var y = logpdf( -0.5, 4.0, 2.0 );
* // returns Number.NEGATIVE_INFINITY
* @example
* var y = logpdf( 1.5, 4.0, 2.0 );
* // returns Number.NEGATIVE_INFINITY
* @example
* var y = logpdf( 0.5, -1.0, 0.5 );
* // returns NaN
* @example
* var y = logpdf( 0.5, 0.5, -1.0 );
* // returns NaN
* @example
* var y = logpdf( NaN, 1.0, 1.0 );
* // returns NaN
* @example
* var y = logpdf( 0.5, NaN, 1.0 );
* // returns NaN
* @example
* var y = logpdf( 0.5, 1.0, NaN );
* // returns NaN
*/
function logpdf( x, alpha, beta ) {
	var lnl;
	if (
		isnan( x ) ||
		isnan( alpha ) ||
		isnan( beta )
	) {
		return NaN;
	}
	if ( alpha <= 0.0 || beta <= 0.0 ) {
		return NaN;
	}
	if ( x < 0.0 || x > 1.0 ) {
		// Support of the Beta distribution: [0,1]
		return NINF;
	}
	else if ( x === 0.0 ) {
		if ( alpha < 1.0 ) {
			return PINF;
		}
		else if ( alpha > 1.0 ) {
			return NINF;
		}
		else {
			return ln( beta );
		}
	}
	else if ( x === 1.0 ) {
		if ( beta < 1.0 ) {
			return PINF;
		}
		else if ( beta > 1.0 ) {
			return NINF;
		}
		else {
			return ln( alpha );
		}
	}
	lnl = ( alpha - 1.0 ) * ln( x ) + ( beta - 1.0 ) * log1p( -x ) - betaln( alpha, beta );
	return lnl;
} // end FUNCTION logpdf()


// EXPORTS //

module.exports = logpdf;
