'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/math/base/utils/is-nonnegative-integer' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );


// MAIN //

/**
* Returns the variance of a binomial distribution.
*
* @param {NonNegativeInteger} n - number of trials
* @param {Probability} p - success probability
* @returns {PositiveNumber} variance
*
* @example
* var v = variance( 100, 0.1 );
* // returns 9
* @example
* var v = variance( 20, 0.5 );
* // returns 5
* @example
* var v = variance( 10.3, 0.5 );
* // returns NaN
* @example
* var v = variance( 20, 1.1 );
* // returns NaN
* @example
* var v = variance( 20, NaN );
* // returns NaN
*/
function variance( n, p ) {
	if (
		isnan( n ) ||
		isnan( p ) ||
		p < 0.0 ||
		p > 1.0 ||
		!isNonNegativeInteger( n ) ||
		n === PINF
	) {
		return NaN;
	}
	return n * p * ( 1.0 - p );
} // end FUNCTION variance()


// EXPORTS //

module.exports = variance;
