#!/usr/bin/env julia

import Distributions: quantile, Cauchy
import JSON

"""
	gen( p, x0, gamma, name )

Generate fixture data and write to file.

# Arguments

* `p`: input value
* `x0`: location parameter
* `gamma`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> p = rand( 1000 );
julia> x0 = rand( 1000 ) * 25.0;
julia> gamma = rand( 1000 ) * 20.0;
julia> gen( p, x0, gamma, \"data.json\" );
```
"""
function gen( p, x0, gamma, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = quantile( Cauchy( x0[i], gamma[i] ), p[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("p", p),
		("x0", x0),
		("gamma", gamma),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Random (negative median):
p = rand( 1000 );
x0 = rand( 1000 ) * -50.0;
gamma = rand( 1000 ) * 20.0;
gen( p, x0, gamma, "negative_median.json" );

# Random (positive median):
p = rand( 1000 );
x0 = rand( 1000 ) * 50.0;
gamma = rand( 1000 ) * 20.0;
gen( p, x0, gamma, "positive_median.json" );

# Random (large gamma):
p = rand( 1000 );
x0 = rand( 1000 ) * 1.0;
gamma = rand( 1000 ) * 50.0;
gen( p, x0, gamma, "large_gamma.json" );
