'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var mgf = require( './../lib' );

var lambda;
var k;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	k = round( randu() * 10.0 );
	lambda = randu() * 10.0;
	t = randu() * lambda;
	y = mgf( t, k, lambda );
	console.log( 't: %d, k: %d, Î»: %d, M_X(t;k,Î»): %d',
		t.toFixed( 4 ),
		k,
		lambda.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
