'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/math/base/utils/is-nonnegative-integer' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Evaluates the moment-generating function (MGF) for an Erlang distribution with shape parameter `k` and rate parameter `lambda` at a value `t`.
*
* @param {number} t - input value
* @param {NonNegativeInteger} k - shape parameter
* @param {PositiveNumber} lambda - rate parameter
* @returns {number} evaluated MGF
*
* @example
* var y = mgf( 0.3, 1, 1.0 );
* // returns ~1.429
* @example
* var y = mgf( 2.0, 2, 3.0 );
* // returns ~8.999
* @example
* var y = mgf( -1.0, 2, 2.0 );
* // returns ~0.444
* @example
* var y = mgf( NaN, 1, 1.0 );
* // returns NaN
* @example
* var y = mgf( 0.0, NaN, 1.0 );
* // returns NaN
* @example
* var y = mgf( 0.0, 1, NaN );
* // returns NaN
* @example
* var y = mgf( 0.2, -2, 0.5 );
* // returns NaN
* @example
* var y = mgf( 0.2, 0.5, 0.5 );
* // returns NaN
* @example
* var y = mgf( 0.2, 1, 0.0 );
* // returns NaN
* @example
* var y = mgf( 0.2, 1, -5.0 );
* // returns NaN
*/
function mgf( t, k, lambda ) {
	if (
		isnan( t ) ||
		!isNonNegativeInteger( k ) ||
		isnan( lambda )||
		lambda < 0.0 ||
		t >= lambda
	) {
		return NaN;
	}
	return pow( 1.0 - (t/lambda), -k );
} // end FUNCTION mgf()


// EXPORTS //

module.exports = mgf;
