'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var entropy = require( './../lib' );

var d1;
var d2;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	d1 = ( randu()*10.0 ) + EPS;
	d2 = ( randu()*20.0 ) + EPS;
	v = entropy( d1, d2 );
	console.log( 'd1: %d, d2: %d, h(X;d1,d2): %d', d1.toFixed( 4 ), d2.toFixed( 4 ), v.toFixed( 4 ) );
}

