#!/usr/bin/env julia

using Distributions
import JSON

"""
	gen( p, d1, d2, name )

Generate fixture data and write to file.

# Arguments

* `p`: input value
* `d1`: numerator degrees of freedom
* `d2`: denominator degrees of freedom
* `name::AbstractString`: output filename

"""
function gen( p, d1, d2, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = quantile( FDist( d1[i], d2[i] ), p[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("p", p),
		("d1", d1),
		("d2", d2),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large d1 parameter:
p = rand( 1000 );
d1 = rand( 1000 ) * 10.0 + 10.0;
d2 = rand( 1000 ) * 10.0;
gen( p, d1, d2, "large_d1.json" );

# Large rate parameter:
p = rand( 1000 );
d1 = rand( 1000 ) * 10.0;
d2 = rand( 1000 ) * 10.0 + 10.0;
gen( p, d1, d2, "large_d2.json" );

# Both large:
p = rand( 1000 );
d2 = rand( 1000 ) * 20.0 + 10.0;
d2 = rand( 1000 ) * 20.0 + 10.0;
gen( p, d1, d2, "both_large.json" );
