'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var gamma = require( '@stdlib/math/base/special/gamma' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );


// MAIN //

/**
* Returns the expected value for a Fr√©chet distribution with shape `alpha`, scale `s`, and location `m`.
*
* @param {PositiveNumber} alpha - shape parameter
* @param {PositiveNumber} s - scale parameter
* @param {number} m - location parameter
* @returns {PositiveNumber} mean
*
* @example
* var y = mean( 5.0, 2.0, 0.0 );
* // returns ~2.328
* @example
* var y = mean( 1.0, 1.0, 0.0 );
* // returns Number.POSITIVE_INFINITY
* @example
* var y = mean( NaN, 1.0, 0.0 );
* // returns NaN
* @example
* var y = mean( 1.0, NaN, 0.0 );
* // returns NaN
* @example
* var y = mean( 1.0, 1.0, NaN );
* // returns NaN
*/
function mean( alpha, s, m ) {
	if (
		isnan( alpha ) ||
		isnan( s ) ||
		isnan( m ) ||
		alpha <= 0.0 ||
		s <= 0.0
	) {
		return NaN;
	}
	if ( alpha <= 1.0 ) {
		// Case: 0 < alpha <= 1
		return PINF;
	}
	return m + ( s * gamma( 1.0 - (1.0/alpha) ) );
} // end FUNCTION mean()


// EXPORTS //

module.exports = mean;
