'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Returns the mode of a geometric distribution.
*
* @param {Probability} p - success probability
* @returns {NonNegativeInteger} mode
*
* @example
* var v = mode( 0.1 );
* // returns 0
* @example
* var v = mode( 0.5 );
* // returns 0
* @example
* var v = mode( 1.1 );
* // returns NaN
* @example
* var v = mode( NaN );
* // returns NaN
*/
function mode( p ) {
	if (
		isnan( p ) ||
		p < 0.0 ||
		p > 1.0
	) {
		return NaN;
	}
	if ( p === 1.0 ) {
		return 1;
	}
	return 0;
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
