'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var mode = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof mode, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for `p`, the function returns `NaN`', function test( t ) {
	var v = mode( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided a success probability `p` outside of `[0,1]`, the function returns `NaN`', function test( t ) {
	var v;

	v = mode( -1.0 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( 1.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( NINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( PINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided `1.0` for `p`, the function returns `1.0`', function test( t ) {
	var v = mode( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns the mode of a geometric distribution', function test( t ) {
	var expected;
	var i;
	var p;
	var y;

	expected = data.expected;
	p = data.p;
	for ( i = 0; i < expected.length; i++ ) {
		y = mode( p[i] );
		t.equal( y, expected[i], 'p: '+p[i]+', y: '+y+', expected: '+expected[i] );
	}
	t.end();
});
