'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var mgf = require( './../lib' );

var beta;
var mu;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu();
	mu = (randu() * 10.0) - 5.0;
	beta = randu() * 20.0;
	y = mgf( t, mu, beta );
	console.log( 't: %d, µ: %d, β: %d, M_X(t;µ,β): %d',
		t.toFixed( 4 ),
		mu.toFixed( 4 ),
		beta.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
