'use strict';

// MAIN //

/**
* Returns the mode of an inverse gamma distribution.
*
* @param {PositiveNumber} alpha - shape parameter
* @param {PositiveNumber} beta - rate parameter
* @returns {PositiveNumber} mode
*
* @example
* var v = mode( 1.0, 1.0 );
* // returns 0.5
* @example
* var v = mode( 4.0, 12.0 );
* // returns 2.4
* @example
* var v = mode( 8.0, 2.0 );
* // returns ~0.222
* @example
* var v = mode( 0.5, 2.0 );
* // returns NaN
* @example
* var v = mode( 1.0, -0.1 );
* // returns NaN
* @example
* var v = mode( -0.1, 1.0 );
* // returns NaN
* @example
* var v = mode( 2.0, NaN );
* // returns NaN
* @example
* var v = mode( NaN, 2.0 );
* // returns NaN
*/
function mode( alpha, beta ) {
	if ( alpha <= 1.0 || beta <= 0.0 ) {
		return NaN;
	}
	return beta / ( alpha + 1.0 );
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
