'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Returns the median of a Kumaraswamy's double bounded distribution.
*
* @param {number} a - first shape parameter
* @param {number} b - second shape parameter
* @returns {number} median
*
* @example
* var v = median( 0.5, 1.0 );
* // returns 0.25
* @example
* var v = median( 4.0, 12.0 );
* // returns ~0.487
* @example
* var v = median( 12.0, 2.0 );
* // returns ~0.903
* @example
* var v = median( 1.0, -0.1 );
* // returns NaN
* @example
* var v = median( -0.1, 1.0 );
* // returns NaN
* @example
* var v = median( 2.0, NaN );
* // returns NaN
* @example
* var v = median( NaN, 2.0 );
* // returns NaN
*/
function median( a, b ) {
	if (
		isnan( a ) ||
		a <= 0.0 ||
		isnan( b ) ||
		b <= 0.0
	) {
		return NaN;
	}
	return pow( 1.0 - pow( 2.0, -1.0/b ), 1.0/a );
} // end FUNCTION median()


// EXPORTS //

module.exports = median;
