#!/usr/bin/env julia

import Distributions: logpdf, Levy
import JSON

"""
	gen( x, mu, c, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `mu`: location parameter
* `c`: scale parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = rand( 1000 ) * 15;
julia> mu = rand( 1000 ) * 10.0;
julia> c = rand( 1000 ) * 5.0;
julia> gen( x, mu, c, "data.json" );
```
"""
function gen( x, mu, c, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = logpdf( Levy( mu[i], c[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("mu", mu),
		("c", c),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Negative mean:
mu = rand( 1000 ) * -10.0;
x = rand( 1000 ) * 10.0 .+ mu;
c = rand( 1000 ) * 5.0 + 1.0;
gen( x, mu, c, "negative_mean.json" );

# Positive mean:
mu = rand( 1000 ) * 10.0;
x = rand( 1000 ) * 10.0 .+ mu;
c = rand( 1000 ) * 5.0 + 1.0;
gen( x, mu, c, "positive_mean.json" );

# Large variance:
mu = rand( 1000 );
x = rand( 1000 ) * 5.0 .+ mu;
c = rand( 1000 ) * 20.0 + 1.0;
gen( x, mu, c, "large_variance.json" );
