'use strict';

var Logistic = require( './../lib' );

var logistic = new Logistic( 2.0, 4.0 );

var mean = logistic.mean;
console.log( 'Mean = %d', mean );
// => Mean = 2.0

var median = logistic.median;
console.log( 'Median = %d', median );
// => Median = 2.0

var s2 = logistic.variance;
console.log( 'Variance = %d', s2 );
// => Variance = 52.637890139143245

var y = logistic.cdf( 0.8 );
console.log( 'F(0.8) = %d', y );
// => F(0.8) = 0.425557483188341

