'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var entropy = require( './../lib' );

var sigma;
var mu;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	mu = ( randu()*10.0 ) - 5.0;
	sigma = randu() * 20.0;
	y = entropy( mu, sigma );
	console.log( 'µ: %d, σ: %d, h(X;µ,σ): %d',
		mu.toFixed( 4 ),
		sigma.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
