#!/usr/bin/env julia

using Distributions
import JSON

"""
	gen( p, mu, sigma, name )

Generate fixture data and write to file.

# Arguments

* `p`: input value
* `mu`: location parameter
* `sigma`: scale parameter
* `name::AbstractString`: output filename

"""
function gen( p, mu, sigma, name )
	z = Array( Float64, length(p) );
	for i in eachindex(p)
		z[ i ] = quantile( LogNormal( mu[i], sigma[i] ), p[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("p", p),
		("mu", mu),
		("sigma", sigma),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Negative location:
p = rand( 1000 );
mu = rand( 1000 ) * -10.0;
sigma = rand( 1000 ) * 5.0;
gen( p, mu, sigma, "negative_location.json" );

# Positive location:
p = rand( 1000 );
mu = rand( 1000 ) * 10.0;
sigma = rand( 1000 ) * 5.0;
gen( p, mu, sigma, "positive_location.json" );

# Large variance:
p = rand( 1000 );
mu = rand( 1000 );
sigma = rand( 1000 ) * 20.0;
gen( p, mu, sigma, "large_variance.json" );
