'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var mgf = require( './../lib' );

var p;
var r;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = (randu() * 1.0) - 0.5;
	r = randu() * 50;
	p = randu();
	y = mgf( t, r, p );
	console.log( 't: %d, r: %d, p: %d, M_X(t;r,p): %d',
		t,
		r.toFixed( 4 ),
		p.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
