'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var skewness = require( './../lib' );


// FIXTURES //

var data = require( './julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof skewness, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var v = skewness( NaN, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = skewness( 10, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `r` which is not a positive number, the function returns `NaN`', function test( t ) {
	var v;

	v = skewness( -2, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = skewness( -1, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = skewness( NINF, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a success probability `p` outside of `[0,1]`, the function returns `NaN`', function test( t ) {
	var v;

	v = skewness( 20, -1.0 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = skewness( 20, 1.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = skewness( 20, NINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = skewness( 20, PINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns the skewness of a negative binomial distribution', function test( t ) {
	var expected;
	var r;
	var p;
	var y;
	var i;

	expected = data.expected;
	r = data.r;
	p = data.p;
	for ( i = 0; i < r.length; i++ ) {
		y = skewness( r[i], p[i] );
		t.equal( y, expected[i], 'r: '+r[i]+', p: '+p[i]+', y: '+y+', expected: '+expected[i] );
	}
	t.end();
});
