'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );


// MAIN //

/**
* Returns the expected value for a normal distribution with mean `mu` and standard deviation `sigma`.
*
* @param {number} mu - mean
* @param {PositiveNumber} sigma - standard deviation
* @returns {number} expected value
*
* @example
* var y = mean( 0.0, 1.0 );
* // returns 0.0
* @example
* var y = mean( 5.0, 2.0 );
* // returns 5.0
* @example
* var y = mean( NaN, 1.0 );
* // returns NaN
* @example
* var y = mean( 0.0, NaN );
* // returns NaN
* @example
* var y = mean( 0.0, 0.0 );
* // returns NaN
*/
function mean( mu, sigma ) {
	if (
		isnan( mu ) ||
		isnan( sigma ) ||
		sigma <= 0.0
	) {
		return NaN;
	}
	return mu;
} // end FUNCTION mean()


// EXPORTS //

module.exports = mean;
