'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for a Pareto distribution with shape parameter `alpha` and scale parameter `beta` at a value `x`.
*
* @param {number} x - input value
* @param {PositiveNumber} alpha - shape parameter
* @param {PositiveNumber} beta - scale parameter
* @returns {Probability} evaluated CDF
*
* @example
* var y = cdf( 2.0, 1.0, 1.0 );
* // returns 0.5
* @example
* var y = cdf( 5.0, 2.0, 4.0 );
* // returns ~0.36
* @example
* var y = cdf( 4.0, 2.0, 2.0 );
* // returns 0.75
* @example
* var y = cdf( 1.9, 2.0, 2.0 );
* // returns 0.0
* @example
* var y = cdf( +Infinity, 4.0, 2.0 );
* // returns 1.0
* @example
* var y = cdf( 2.0, -1.0, 0.5 );
* // returns NaN
* @example
* var y = cdf( 2.0, 0.5, -1.0 );
* // returns NaN
* @example
* var y = cdf( NaN, 1.0, 1.0 );
* // returns NaN
* @example
* var y = cdf( 0.0, NaN, 1.0 );
* // returns NaN
* @example
* var y = cdf( 0.0, 1.0, NaN );
* // returns NaN
*/
function cdf( x, alpha, beta ) {
	if (
		isnan( x ) ||
		isnan( alpha ) ||
		isnan( beta )
	) {
		return NaN;
	}
	if ( alpha <= 0.0 || beta <= 0.0 ) {
		return NaN;
	}
	if ( x < beta ) {
		return 0.0;
	}
	return 1.0 - pow( beta / x, alpha );
} // end FUNCTION cdf()


// EXPORTS //

module.exports = cdf;
