'use strict';

var T = require( './../lib' );

var t = new T( 2.0 );

var mu = t.mean;
console.log( 'Mean = %d', mu );
// => Mean = 0.0

var mode = t.mode;
console.log( 'Mode = %d', mode );
// => Mode = 0.0

var s2 = t.variance;
console.log( 'Variance = %d', s2 );
// => Variance = Infinity

var y = t.cdf( 0.8 );
console.log( 'F(0.8) = %d', y );
// => F(0.8) = 0.7461829819586655

