'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );


// MAIN //

/**
* Returns the skewness of a Student's t distribution.
*
* @param {NonNegativeNumber} v - degrees of freedom
* @returns {NonNegativeNumber} skewness
*
* @example
* var v = skewness( 9.0 );
* // returns 0.0
* @example
* var v = skewness( 2.0 );
* // returns 0.0
* @example
* var v = skewness( 0.5 );
* // returns NaN
* @example
* var v = skewness( -0.2 );
* // returns NaN
* @example
* var v = skewness( NaN );
* // returns NaN
*/
function skewness( v ) {
	if ( isnan( v ) || v <= 3.0 ) {
		return NaN;
	}
	return 0.0;
} // end FUNCTION skewness()


// EXPORTS //

module.exports = skewness;
