'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Returns the mode of a triangular distribution.
*
* @param {number} a - minimum support
* @param {number} b - maximum support
* @param {number} c - mode
* @returns {number} mode
*
* @example
* var v = mode( 0.0, 1.0, 0.5 );
* // returns 0.5
* @example
* var v = mode( 4.0, 12.0, 9.0 );
* // returns 9.0
* @example
* var v = mode( -4.0, 4.0, -1.0 );
* // returns -1.0
* @example
* var v = mode( 1.0, -0.1, 0.5 );
* // returns NaN
* @example
* var v = mode( 0.0, 1.0, 2.0 );
* // returns NaN
* @example
* var v = mode( NaN, 4.0, 2.0 );
* // returns NaN
* @example
* var v = mode( 0.0, NaN, 2.0 );
* // returns NaN
* @example
* var v = mode( 0.0, 4.0, NaN );
* // returns NaN
*/
function mode( a, b, c ) {
	if (
		isnan( a ) ||
		isnan( b ) ||
		isnan( c ) ||
		!( a <= c && c <= b )
	) {
		return NaN;
	}
	return c;
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
