'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Evaluates the probability density function (PDF) for a triangular distribution with lower limit `a` and upper limit `b` and mode `c` at a value `x`.
*
* @param {number} x - input value
* @param {number} a - lower limit
* @param {number} b - upper limit
* @param {number} c - mode
* @returns {number} evaluated PDF
*
* @example
* var y = pdf( 0.5, -1.0, 1.0, 0.0 );
* // returns 0.5
* @example
* var y = pdf( 0.5, -1.0, 1.0, 0.5 );
* // returns 1.0
* @example
* var y = pdf( -10.0, -20.0, 0.0, -2.0 );
* // returns ~0.278
* @example
* var y = pdf( -2.0, -1.0, 1.0, 0.0 );
* // returns 0.0
* @example
* var y = pdf( NaN, 0.0, 1.0, 0.5 );
* // returns NaN
* @example
* var y = pdf( 0.0, NaN, 1.0, 0.5 );
* // returns NaN
* @example
* var y = pdf( 0.0, 0.0, NaN, 0.5 );
* // returns NaN
* @example
* var y = pdf( 2.0, 1.0, 0.0, NaN );
* // returns NaN
* @example
* var y = pdf( 2.0, 1.0, 0.0, 1.5 );
* // returns NaN
*/
function pdf( x, a, b, c ) {
	var denom1;
	var denom2;
	var denom3;

	if (
		isnan( x ) ||
		isnan( a ) ||
		isnan( b ) ||
		isnan( c )
	) {
		return NaN;
	}
	if ( !( a <= c && c <= b ) ) {
		return NaN;
	}
	if ( x < a ) {
		return 0.0;
	}
	denom1 = ( b - a ) * ( c - a );
	denom2 = b - a;
	denom3 = ( b - a ) * ( b - c );
	// Case: x >= a
	if ( x < c ) {
		return 2.0 * ( x - a ) / denom1;
	}
	if ( x === c ) {
		return 2.0 / denom2;
	}
	// Case: x > c
	if ( x <= b ) {
		return 2.0 * ( b - x ) / denom3;
	}
	// Case: x > b
	return 0.0;
} // end FUNCTION pdf()


// EXPORTS //

module.exports = pdf;
