'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );
var exp = require( '@stdlib/math/base/special/exp' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );


// MAIN //

/**
* Evaluates the probability density function (PDF) for a Weibull distribution with scale parameter `lambda` and shape parameter `k` at a value `x`.
*
* @param {number} x - input value
* @param {PositiveNumber} lambda - scale parameter
* @param {PositiveNumber} k - shape parameter
* @returns {number} evaluated probability density function
*
* @example
* var y = pdf( 2.0, 0.5, 1.0 );
* // returns ~0.037
* @example
* var y = pdf( 0.1, 1.0, 1.0 );
* // returns ~0.905
* @example
* var y = pdf( -1.0, 4.0, 2.0 );
* // returns 0.0
* @example
* var y = pdf( NaN, 0.6, 1.0 );
* // returns NaN
* @example
* var y = pdf( 0.0, NaN, 1.0 );
* // returns NaN
* @example
* var y = pdf( 0.0, 0.0, NaN );
* // returns NaN
* @example
* var y = pdf( 2.0, 0.0, -1.0 );
* // returns NaN
*/
function pdf( x, lambda, k ) {
	var xol;
	var z;
	if ( isnan( lambda ) || isnan( k ) ) {
		return NaN;
	}
	if ( lambda <= 0.0 || k <= 0.0 ) {
		return NaN;
	}
	if ( x < 0.0 ) {
		return 0.0;
	}
	if ( x === PINF || x === NINF ) {
		return 0.0;
	}
	if ( x === 0.0 ) {
		return ( k === 1.0 ) ? k/lambda : 0.0;
	}
	xol = x / lambda;
	z = pow( xol, k - 1.0 );
	return ( k / lambda ) * z * exp( -pow( xol, k ) );
} // end FUNCTION pdf()


// EXPORTS //

module.exports = pdf;
