'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var cdf = require( './../lib' );

var a;
var b;
var x;
var y;
var i;

for ( i = 0; i < 25; i++ ) {
	x = ( randu()*20.0 ) - 10.0;
	a = ( randu()*20.0 ) - 20.0;
	b = a + ( randu()*40.0 );
	y = cdf( x, a, b );
	console.log( 'x: %d, a: %d, b: %d, F(x;a,b): %d',
		x.toFixed( 4 ),
		a.toFixed( 4 ),
		b.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
