'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var cdf = require( './../lib' );

var i;
var n;
var p;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 20.0;
	n = round( randu() * 100.0 );
	p = randu();
	y = cdf( x, n, p );
	console.log( 'x: %d, n: %d, p: %d, F(x;n,p): %d', x.toFixed( 4 ), n, p.toFixed( 4 ), y.toFixed( 4 ) );
}
