'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var mean = require( './../lib' );

var a;
var b;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	a = round( randu()*10.0 );
	b = round( ( randu()*10.0 ) + a );
	v = mean( a, b );
	console.log( 'a: %d, b: %d, E(X;a,b): %d', a.toFixed( 4 ), b.toFixed( 4 ), v.toFixed( 4 ) );
}

