'use strict';

var randint = require( '@stdlib/random/base/discrete-uniform' );
var pmf = require( './../lib' );

var randa = randint.factory( 0, 10 );
var randb = randint.factory();
var a;
var b;
var x;
var y;
var i;

for ( i = 0; i < 25; i++ ) {
	a = randa();
	x = randb( a, a+randa() );
	b = randb( a, a+randa() );
	y = pmf( x, a, b );
	console.log( 'x: %d, a: %d, b: %d, P(X=x;a,b): %d', x.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), y.toFixed( 4 ) );
}
