'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var logpdf = require( './../lib' );

var lambda;
var k;
var x;
var y;
var i;

for ( i = 0; i < 20; i++ ) {
	x = randu() * 10.0;
	k = round( randu() * 10.0 );
	lambda = randu() * 5.0;
	y = logpdf( x, k, lambda );
	console.log( 'x: %d, k: %d, Î»: %d, ln(f(x;k,Î»)): %d', x.toFixed( 4 ), k, lambda.toFixed( 4 ), y.toFixed( 4 ) );
}
