'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var quantile = require( './../lib' );

var lambda;
var k;
var p;
var y;
var i;

for ( i = 0; i < 20; i++ ) {
	p = randu();
	k = round( randu() * 10.0 );
	lambda = randu() * 5.0;
	y = quantile( p, k, lambda );
	console.log( 'p: %d, k: %d, Î»: %d, Q(p;k,Î»): %d',
		p.toFixed( 4 ),
		k,
		lambda.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
