'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var mean = require( './../lib' );

var d1;
var d2;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	d1 = ( randu()*10.0 ) + EPS;
	d2 = ( randu()*10.0 ) + EPS;
	v = mean( d1, d2 );
	console.log( 'd1: %d, d2: %d, E(X;d1,d2): %d', d1.toFixed( 4 ), d2.toFixed( 4 ), v.toFixed( 4 ) );
}

