'use strict';

// MAIN //

/**
* Returns the mode of a F distribution.
*
* @param {PositiveNumber} d1 - numerator degrees of freedom
* @param {PositiveNumber} d2 - denominator degrees of freedom
* @returns {PositiveNumber} mode
*
* @example
* var v = mode( 3.0, 5.0 );
* // returns ~0.238
* @example
* var v = mode( 4.0, 12.0 );
* // returns ~0.429
* @example
* var v = mode( 8.0, 4.0 );
* // returns 0.5
* @example
* var v = mode( 3.0, -0.1 );
* // returns NaN
* @example
* var v = mode( -0.1, 1.0 );
* // returns NaN
* @example
* var v = mode( 3.0, NaN );
* // returns NaN
* @example
* var v = mode( NaN, 2.0 );
* // returns NaN
*/
function mode( d1, d2 ) {
	if ( d1 <= 2.0 || d2 <= 0.0 ) {
		return NaN;
	}
	return ( ( d1-2.0 ) / d1 ) * ( d2 / ( d2+2.0 ) );
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
