'use strict';

var randu = require( '@stdlib/random/base/randu' );
var pdf = require( './../lib' );

var d1;
var d2;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 4.0;
	d1 = randu() * 10.0;
	d2 = randu() * 10.0;
	y = pdf( x, d1, d2 );
	console.log( 'x: %d, d1: %d, d2: %d, f(x;d1,d2): %d',
		x.toFixed( 4 ),
		d1.toFixed( 4 ),
		d2.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
