'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var median = require( './../lib' );

var alpha;
var m;
var s;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	alpha = ( randu()*20.0 ) + EPS;
	s = ( randu()*20.0 ) + EPS;
	m = ( randu()*20.0 ) - 40.0;
	y = median( alpha, s, m );
	console.log( 'Î±: %d, s: %d, m: %d, Median(X;Î±,s,m): %d',
		alpha.toFixed( 4 ),
		s.toFixed( 4 ),
		m.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
