'use strict';

var Gamma = require( './../lib' );

var gamma = new Gamma( 2.0, 4.0 );

var mu = gamma.mean;
console.log( 'Mean = %d', mu );
// => Mean = 0.5

var mode = gamma.mode;
console.log( 'Mode = %d', mode );
// => Mode = 0.25

var s2 = gamma.variance;
console.log( 'Variance = %d', s2 );
// => Variance = 0.124

var y = gamma.cdf( 0.8 );
console.log( 'F(0.8) = %d', y );
// => F(0.8) = ~0.829

