'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var logpdf = require( './../lib' );

var alpha;
var beta;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 3.0;
	alpha = randu() * 5.0;
	beta = randu() * 5.0;
	y = logpdf( x, alpha, beta );
	console.log( 'x: %d, α: %d, β: %d, ln(f(x;α,β)): %d',
		x.toFixed( 4 ),
		alpha.toFixed( 4 ),
		beta.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
