'use strict';

var randu = require( '@stdlib/random/base/randu' );
var mgf = require( './../lib' );

var alpha;
var beta;
var t;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu() * 3.0;
	alpha = randu() * 5.0;
	beta = randu() * 5.0;
	y = mgf( t, alpha, beta );
	console.log( 't: %d, α: %d, β: %d, M_X(t;α,β): %d',
		t.toFixed( 4 ),
		alpha.toFixed( 4 ),
		beta.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
