'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pmf = require( './../lib' );

var p;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = round( randu() * 5.0 );
	p = randu();
	y = pmf( x, p );
	console.log( 'x: %d, p: %d, P( X = x; p ): %d',
		x,
		p.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
