'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var p;
var r;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	r = randu();
	p = randu();
	y = quantile( r, p );
	console.log( 'r: %d, p: %d, Q(r;p): %d', r.toFixed( 4 ), p.toFixed( 4 ), y.toFixed( 4 ) );
}
