'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var entropy = require( './../lib' );

var beta;
var mu;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	mu = ( randu()*10.0 ) - 5.0;
	beta = randu() * 20.0;
	y = entropy( mu, beta );
	console.log( 'µ: %d, β: %d, h(X;µ,β): %d',
		mu.toFixed( 4 ),
		beta.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
